import 'package:cubit_example/constants/button_drawer.dart';
import 'package:cubit_example/constants/theme/app_colors.dart';
import 'package:cubit_example/constants/theme/app_typography.dart';
import 'package:cubit_example/data/mock_data.dart';
import 'package:e_commerce/constants/button_drawer.dart';
import 'package:e_commerce/constants/favorite_row.dart';
import 'package:e_commerce/data/cart_info.dart';
import 'package:e_commerce/data/mock_data.dart';
import 'package:e_commerce/theme/app_colors.dart';
import 'package:e_commerce/theme/app_typography.dart';
import 'package:flutter/material.dart';

class FavoritePage extends StatefulWidget {
  const FavoritePage({super.key});

  @override
  State<FavoritePage> createState() => _FavoritePageState();
}

class _FavoritePageState extends State<FavoritePage> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: AppColors.veryLightGrey,
      body: SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.only(
            left: 30,
            top: 42,
            right: 30,
          ),
          child: Column(
            children: [
              const Row(
                children: [
                  ButtonDrawer(),
                  SizedBox(width: 53),
                  Text(
                    'Favorites',
                    style: AppTypography.favoriteTitle,
                  )
                ],
              ),
              isFavoriteItems.isEmpty
                  ? const Padding(
                      padding: EdgeInsets.only(top: 50),
                      child: Center(
                        child: Text(
                          'There is no Favorite product here!',
                          style: TextStyle(fontSize: 20),
                        ),
                      ),
                    )
                  : ListView.separated(
                      shrinkWrap: true,
                      primary: false,
                      itemBuilder: (context, index) {
                        return FavoriteRow(
                          onTapCart: () {
                            setState(
                              () {
                                var isCartItem = CartInfo(
                                  productInfo: isFavoriteItems[index],
                                  color: isFavoriteItems[index].selectedColor!,
                                  count: 1,
                                );
                                var checkCartItem = isCartItems.any((i) =>
                                    i.productInfo.id ==
                                    isCartItem.productInfo.id);
                                if (!checkCartItem) {
                                  isCartItems.add(isCartItem);
                                }
                                print(isCartItems.length);
                              },
                            );
                          },
                          onTap: () {
                            setState(() {
                              isFavoriteItems.remove(isFavoriteItems[index]);
                            });
                          },
                          color: isFavoriteItems[index].selectedColor!.color,
                          textName: isFavoriteItems[index].name,
                          picture: isFavoriteItems[index].imagePath,
                          price: isFavoriteItems[index].price,
                        );
                      },
                      separatorBuilder: (context, index) {
                        return const SizedBox(
                          height: 10,
                        );
                      },
                      itemCount: isFavoriteItems.length,
                    ),
            ],
          ),
        ),
      ),
    );
  }
}
